%% Add_NewROI()%% mida group http://mida.dima.unige.it - 2010/2015%%%% this function add a new ROI%%%% called by main_gui(action,varargin) function Add_NewROI()global pet_gui;id=num2str(pet_gui.PANELroi.ROIId(end-1)+1);newroi=inputdlg({'ROI Name:','ROI Id:'},'New ROI',1,{'',id});if ~isempty(newroi)    pet_gui.PANELroi.ROIName{end}=newroi{1};    pet_gui.PANELroi.ROIName{end+1}='New ROI..........';    pet_gui.PANELroi.ROIId(end)=str2double(newroi{2});    pet_gui.PANELroi.ROIId(end+1)=-1;    Initialize_ROI(pet_gui.PopupValue);    set(pet_gui.PANELroi.popup,'string',pet_gui.PANELroi.ROIName);    set(pet_gui.PANELroi.popup,'value',pet_gui.PopupValue+1);    PopUp_Districtsendend