%% Gui_Initialization()%% mida group http://mida.dima.unige.it - 2010/2015%%%% this function sets the inital parameter for the gui, enabling buttons and so on%%%% called by: Load_Data()function Gui_Initialization()global pet_gui;set(pet_gui.PANELax.panel,'visible','off');set(pet_gui.PANELax.SLIDERimage,'value',0);set(pet_gui.PANELax.TXT,'string','');set(pet_gui.PANELroi.popup,'value',1,'enable','off');set(pet_gui.PANELroi.PANEL2.PB1,'enable','off');set(pet_gui.PANELroi.PANEL2.PB2,'enable','off');set(pet_gui.PANELroi.PANEL2.PB3,'enable','off');set(pet_gui.PANELroi.PANEL2.PB4,'enable','off');set(pet_gui.PANELroi.PANEL3.TXT1a,'enable','off');set(pet_gui.PANELroi.PANEL3.PB2,'enable','off');set(pet_gui.PANELroi.PANEL3.TXT3a,'enable','off');set(pet_gui.PANELroi.PANEL3.TXT1b,'string','');set(pet_gui.PANELroi.PANEL3.TXT2b,'string','');set(pet_gui.PANELroi.PANEL3.TXT3b,'string','');set(pet_gui.PANELroi.PANEL4.CKBOX1,'value',0,'enable','off');set(pet_gui.PANELroi.PANEL4.CKBOX2,'value',0,'enable','off');set(pet_gui.PANELroi.PANEL4.CKBOX3,'value',0,'enable','off');set(pet_gui.PANELroi.PANEL5.PB1,'enable','off');set(pet_gui.PANELroi.PANEL5.PB2,'enable','off');set(pet_gui.PANELroi.PANEL5.PB3,'enable','off');set(pet_gui.PBstart,'enable','off');end